import{_ as s,o as e,c as r,R as t}from"./chunks/framework.Rav5NEMf.js";const i="/vitepress-librenms/assets/2024-06-20-16-23-16._WwkdkaV.png",p="/vitepress-librenms/assets/2024-06-20-16-24-41.sbvNSvgv.png",n="/vitepress-librenms/assets/2024-06-20-16-34-02.sMmgQP2w.png",o="/vitepress-librenms/assets/2024-06-20-16-35-19.oI-4B9oN.png",a="/vitepress-librenms/assets/2024-06-20-16-36-59.wvkbuTct.png",m="/vitepress-librenms/assets/2024-06-20-16-38-06.s1GY1Tko.png",l="/vitepress-librenms/assets/2024-06-20-16-39-28.nHNrCa8M.png",c="/vitepress-librenms/assets/2024-06-20-19-35-23.op9lRAYD.png",_="/vitepress-librenms/assets/2024-06-20-19-36-39.yOKXiJ77.png",b="/vitepress-librenms/assets/2024-06-20-19-38-27.S2m-9wt2.png",g="/vitepress-librenms/assets/2024-06-20-19-42-55.pMGurAj-.png",L=JSON.parse('{"title":"使用 Proxmox 安裝 Librenms 範本","description":"","frontmatter":{},"headers":[],"relativePath":"librenms/120-proxmox-ct/index.md","filePath":"librenms/120-proxmox-ct/index.md"}'),x={name:"librenms/120-proxmox-ct/index.md"},d=t('<h1 id="使用-proxmox-安裝-librenms-範本" tabindex="-1">使用 Proxmox 安裝 Librenms 範本 <a class="header-anchor" href="#使用-proxmox-安裝-librenms-範本" aria-label="Permalink to &quot;使用 Proxmox 安裝 Librenms 範本&quot;">​</a></h1><p>Proxmox VE 是一款基於Debian 為核心的開源伺服器虛擬化平台，非常推薦大家使用。</p><p>虛擬機 (KVM) 技術因為完整模擬整個主機系統，所以非常耗資源，建議改用 LXC 技術的虛擬容器(CT)，耗用的資源非常少又快速。</p><h2 id="下載-librenms-ct-範本" tabindex="-1">下載 librenms ct 範本 <a class="header-anchor" href="#下載-librenms-ct-範本" aria-label="Permalink to &quot;下載 librenms ct 範本&quot;">​</a></h2><p>因為 Librenms 官方沒有提供 CT 的範本，以下提供自製的範本，供大家快速安裝。</p><p>CT 範本下載： <a href="https://drive.google.com/file/d/1OyPnE5DgbUiLwi8FEwohUtNH0kMxJIgB/view?usp=drive_link" target="_blank" rel="noreferrer">lbrenms-20240620.tar.gz</a></p><h2 id="建立-ct-虛擬容器" tabindex="-1">建立 CT 虛擬容器 <a class="header-anchor" href="#建立-ct-虛擬容器" aria-label="Permalink to &quot;建立 CT 虛擬容器&quot;">​</a></h2><ol><li><p>將範本檔上傳到 proxmox 主機 <img src="'+i+'" alt=""></p></li><li><p>右上角建立一個新的 CT</p></li></ol><p><img src="'+p+'" alt=""></p><p><img src="'+n+'" alt=""></p><p>選取剛剛上傳的範本 <img src="'+o+'" alt=""></p><p>設定要給這個容器多少硬碟空間 <img src="'+a+'" alt=""></p><p>要給容器的 CPU 核心數，librenms 不需要給太多 1 或2 都可以 <img src="'+m+'" alt=""></p><p>要給容器的 cpu 核心數跟記憶體都是隨時可以修改，而且容器都不需要中斷執行，所以可以以後觀察使用的負載情況去調整即可。 <img src="'+l+'" alt=""></p><p>每個容器都有一個獨立的 ip，這裡以 10.1.1.6 做範例。 如果要支援 ipv6 也可以設定好。 防火牆建議可以先取消勾選，省的遇到其他被封鎖的問題 <img src="'+c+'" alt=""></p><p><img src="'+_+'" alt=""></p><p>最後一步，按下完成後，就會建立 CT 並且開始執行 <img src="'+b+'" alt=""></p><p>最後使用瀏覽器開啟 <a href="http://10.1.1.6" target="_blank" rel="noreferrer">http://10.1.1.6</a> 使用範本預設的帳號、密碼 librenms 。就可以開使用強大的 Librenms了。 <img src="'+g+'" alt=""></p>',18),h=[d];function v(f,u,T,P,k,C){return e(),r("div",null,h)}const N=s(x,[["render",v]]);export{L as __pageData,N as default};
