import{_ as s,o as t,c as e,R as a}from"./chunks/framework.Rav5NEMf.js";const i="/vitepress-librenms/assets/2023-12-14-12-23-53.H4gYawpS.png",n="/vitepress-librenms/assets/2023-12-12-08-49-03.fmPzxgfn.png",u="/vitepress-librenms/assets/2023-12-12-08-49-52.xBI0p-yV.png",r="/vitepress-librenms/assets/2023-12-12-08-50-07.s-3hv5uK.png",l="/vitepress-librenms/assets/2023-12-14-14-10-57.7NCUhWOk.png",p="/vitepress-librenms/assets/2023-12-15-13-47-41.8piR39PR.png",o="/vitepress-librenms/assets/2023-12-15-13-55-00.oN6kTJYv.png",d="/vitepress-librenms/assets/2023-12-15-13-58-51.Q7PZM5Iz.png",c="/vitepress-librenms/assets/2023-12-12-08-50-31.EKM3eTY5.png",h="/vitepress-librenms/assets/2023-12-12-08-51-47.fHYlPtPa.png",b="/vitepress-librenms/assets/2023-12-12-08-51-58.D-roG7Bp.png",C=JSON.parse('{"title":"Lubuntu 22.04 安裝","description":"","frontmatter":{},"headers":[],"relativePath":"librenms/20-install-lubuntu/index.md","filePath":"librenms/20-install-lubuntu/index.md"}'),m={name:"librenms/20-install-lubuntu/index.md"},_=a('<h1 id="lubuntu-22-04-安裝" tabindex="-1">Lubuntu 22.04 安裝 <a class="header-anchor" href="#lubuntu-22-04-安裝" aria-label="Permalink to &quot;Lubuntu 22.04 安裝&quot;">​</a></h1><h2 id="製作-lubuntu-安裝開機-usb" tabindex="-1">製作 lubuntu 安裝開機 usb <a class="header-anchor" href="#製作-lubuntu-安裝開機-usb" aria-label="Permalink to &quot;製作 lubuntu 安裝開機 usb&quot;">​</a></h2><ol><li>下載 lubuntu 22.04 iso 檔，使用軟體製作 usb 開機檔</li></ol><p>lubuntu 22.04 (<a href="https://lubuntu.me/downloads/" target="_blank" rel="noreferrer">https://lubuntu.me/downloads/</a>)</p><p>rusuf (<a href="https://rufus.ie/zh_TW/" target="_blank" rel="noreferrer">https://rufus.ie/zh_TW/</a>)</p><h2 id="安裝-lubuntu" tabindex="-1">安裝 lubuntu <a class="header-anchor" href="#安裝-lubuntu" aria-label="Permalink to &quot;安裝 lubuntu&quot;">​</a></h2><p>1.使用 usb 開機，執行第一個選項，就會先以 live cd 的模式進入 lubuntu 作業系統的界面，點選桌面的 install Lubuntu 圖示，就會開始安裝 lubuntu <img src="'+i+'" alt=""></p><p><img src="'+n+'" alt="install_lubuntu"></p><p>2.依照安裝指示一直按下一步即可，以下僅顯示需要做選擇或輸入的部份提示 <img src="'+u+'" alt=""></p><p><img src="'+r+'" alt=""></p><p>安裝完成後，移除 usb 後重新開機</p><h2 id="安裝-guest-additions" tabindex="-1">安裝 Guest Additions <a class="header-anchor" href="#安裝-guest-additions" aria-label="Permalink to &quot;安裝 Guest Additions&quot;">​</a></h2><p>為了讓 host主控端要跟虛擬機（guest端）做進階的功能聯繫，所以虛擬機需要安裝 virtualbox 的 guest addition 程式</p><p><img src="'+l+'" alt=""></p><p><img src="'+p+'" alt=""></p><p>因為必須使用 root 權限執行，我們可以開啟終端機視窗，從檔案總管開啟終端機視窗，會直接跳到目前所在目錄 <img src="'+o+'" alt=""></p><p>執行以下命列， sudo 允許一般用戶以 root 或其他特定帳號執行程式</p><div class="language-shell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./VBoxLinuxAdditions.run</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> `</span></span></code></pre></div><p><img src="'+d+'" alt=""> 安裝完成後請重新開機，重開機後 guest 就支援畫面更大的解析度，以及 host 跟 guest 雙向剪貼簿功能，可以在一邊複製後，貼到另一邊</p><h2 id="安裝中文輸入法-在終端機-命令列-視窗內" tabindex="-1">安裝中文輸入法，在終端機（命令列）視窗內， <a class="header-anchor" href="#安裝中文輸入法-在終端機-命令列-視窗內" aria-label="Permalink to &quot;安裝中文輸入法，在終端機（命令列）視窗內，&quot;">​</a></h2><p><img src="'+c+'" alt=""></p><p>執行以下指令安裝中文輸入法， apt 是 ubuntu 、 debian 系統的套件安裝程式。</p><p><code>sudo apt install fcitx5-chewing</code></p><p>備註： 不使用命令列安裝軟體，從功能表執行「系統工具/Muon套件管理員」 <img src="'+h+'" alt=""></p><p><img src="'+b+'" alt=""></p><h2 id="常用軟體推薦-請試著自己安裝看看" tabindex="-1">常用軟體推薦，請試著自己安裝看看 <a class="header-anchor" href="#常用軟體推薦-請試著自己安裝看看" aria-label="Permalink to &quot;常用軟體推薦，請試著自己安裝看看&quot;">​</a></h2><p>gimp、inkscape、krita... 我就喜歡這種 linux 跟 windows 都有的應用程式，用久就會發現「作業系統」不是重點，應用程式才是。</p>',27),g=[_];function k(f,v,x,P,q,T){return t(),e("div",null,g)}const F=s(m,[["render",k]]);export{C as __pageData,F as default};
